mapper.select:
select postId,nickName,title,status,productsPrice,hitCount,contents,
created,deadLine,chooseFile,postLikeCount,recruitment,type,categoryId,myAddr,userId,
likeDate from post where postId=#{postId}

mapper.update:
update post set title=#{title},myAddr=#{myAddr},
productsPrice=#{productsPrice},contents=#{contents},
recruitment=#{recruitment},type=#{type},categoryId=#{categoryId}
where postId=#{postId}
