mapper.select:
select postId,nickName,title,status,productsPrice,hitCount,contents,
created,deadLine,chooseFile,postLikeCount,recruitment,categoryId,myAddr,participant,
likeDate,userId from post where postId=#{postId}

mapper.update:
update post set title=#{title},myAddr=#{myAddr},
productsPrice=#{productsPrice},contents=#{contents},
recruitment=#{recruitment},categoryId=#{categoryId},
deadLine=#{deadLine},status=#{status}
where postId=#{postId}

mapper.select:
SELECT postId, title, productsPrice, hitCount, userId, created, deadLine, chooseFile, postLikeCount, recruitment, categoryId, myAddr FROM post
WHERE deadLine <= DATEADD('DAY', 7, CURRENT_TIMESTAMP())
ORDER BY deadLine DESC LIMIT 5;

select nvl(count(*),0) from (
	select * from post where userId = #{userId} 
	and participant > 1 and status = '모집중')
